-- Seed data for trends table (2026 hairstyle trends)

INSERT OR IGNORE INTO trends (id, trend_name, description, category, popularity_score, keywords) VALUES
(1, 'Textured Crop', 'Potongan pendek dengan tekstur alami di atas, cocok untuk wajah bulat dan oval. Low maintenance dan professional.', 'trending', 95, '["textured", "crop", "modern", "professional", "low-maintenance"]'),
(2, 'Classic Pompadour', 'Gaya klasik dengan volume tinggi di depan, cocok untuk wajah bulat. Membutuhkan pomade atau wax.', 'classic', 85, '["pompadour", "classic", "volume", "formal", "retro"]'),
(3, 'Fade + Quiff', 'Kombinasi fade di samping dengan quiff di atas. Cocok untuk segala bentuk wajah, modern dan stylish.', 'trending', 90, '["fade", "quiff", "modern", "versatile", "stylish"]'),
(4, 'French Crop', 'Potongan pendek dengan poni lurus, cocok untuk wajah kotak dan oval. Trendy dan mudah diatur.', 'modern', 88, '["french-crop", "short", "fringe", "trendy", "easy"]'),
(5, 'Undercut Slick Back', 'Undercut dengan rambut atas disisir ke belakang. Cocok untuk wajah lonjong dan oval, sangat formal.', 'classic', 80, '["undercut", "slick-back", "formal", "professional", "elegant"]'),
(6, 'Buzz Cut', 'Potongan sangat pendek, praktis dan maskulin. Cocok untuk wajah kotak, tidak cocok untuk wajah bulat.', 'classic', 70, '["buzz-cut", "short", "minimal", "masculine", "practical"]'),
(7, 'Side Part', 'Gaya klasik dengan belahan samping. Cocok untuk wajah kotak, sangat professional untuk kantoran.', 'classic', 75, '["side-part", "classic", "professional", "formal", "office"]'),
(8, 'Messy Fringe', 'Poni acak dengan tekstur natural. Cocok untuk wajah oval dan lonjong, gaya kasual dan youthful.', 'modern', 85, '["fringe", "messy", "casual", "youthful", "texture"]'),
(9, 'Korean Two-Block', 'Gaya Korea dengan volume di atas dan pendek di samping. Trendy 2026, cocok untuk wajah bulat dan oval.', 'trending', 92, '["two-block", "korean", "trendy", "volume", "modern"]'),
(10, 'Taper Fade + Texture', 'Taper fade dengan tekstur di atas. Versatile untuk semua bentuk wajah, sangat populer.', 'trending', 93, '["taper-fade", "texture", "versatile", "popular", "modern"]');
